#!/bin/bash
ts=$(date +%s%N)
curl --data-binary '{"jsonrpc": "2.0", "method": "secretstore_encrypt", "params": ["0x3fcbc7dd59aaaf93c9530b13868d732ffb1621a1", "alicepwd", "", ""], "id":1 }' -H 'content-type: application/json' http://127.0.0.1:8545/
echo "Time taken to encrypt the document is: $((($(date +%s%N) - $ts)/1000000)) milliseconds"
